require File.expand_path(File.dirname(__FILE__) + '/../lib/problem8')

describe Problem8 do

  describe "transform input into substrings" do
    it "'123456' with size 5 must return ['12345', '23456']" do
      subject.to_consecutive_digits("123456", 5).should == ["12345", "23456"]
    end
    it "a string with 50 chars, divide 5 by 5 must return 46 entries" do
      string = '73167176531331624919225119674426574742355349194934'
      subject.to_consecutive_digits(string, 5).size.should == 46
    end
  end

  describe "filtering generated entries" do
    it "remove any entry that contains a zero" do
      subject.to_consecutive_digits('1234506', 5).should == ['12345']
    end
  end

  describe "greatest product of five consecutive digits" do

    before(:all) do
      @big_entry = ''
    end

    it "'123456' must return 720" do
      subject.greatest_product_of_consecutive_digits("123456").should == 720
    end

    it "for big entry greatest product of five consecutive digits is 40824" do
      subject.greatest_product_of_consecutive_digits(@big_entry).should == 40824
    end
  end
end

